import React, { Component }from 'react'; 
import api from '../../services/api';
import "./styles.css";


export default class Main extends Component {

    state = {
        products: [],
        productInfo: {},
        page: 1,
    }
    componentDidMount() {
        this.loadProducts();
    }

    loadProducts = async (page = 1) => {
        const response = await api.get(`/products?page=${page}`);
        // console.log(response.data.docs);
        const { docs, ...productInfo } = response.data;
        this.setState({ products: docs, productInfo, page})
    }

    prevPage = () => {
        const { page, productInfo } = this.state;
        if(page === 1) return;
        const pageNumber = page - 1;
        this.loadProducts(pageNumber);
    }

    nextPage = () => {
        const { page, productInfo } = this.state;
        if( page === productInfo.pages ) return;
        const pageNumber = page + 1;
        this.loadProducts(pageNumber);
    }

    render(){
        const {products, page, productInfo} = this.state;
        return (
            <React.Fragment>

                <div className="product-list">
                    <div className="display-item">Listing: {this.state.products.length} item(s)</div>
                    {products.map(product => (
                        <article key={product._id}>
                            <strong>{product.title}</strong>
                            <p>{product.description}</p>
                            <a href="">More Info</a>
                        </article>
                    ))}
                    <div className="actions">
                        <button disabled={page === 1} onClick={this.prevPage}>Preview</button>
                        <button disabled={page === productInfo.pages} onClick={this.nextPage}>Next</button>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}
